import React, { useEffect, useState } from "react";
import { Container, Grid, FormControl, InputLabel, Input, Button } from "@material-ui/core";
import ServicoService from "../../services/ServicoService";
import Servico from "./Serviço";
import { DialogService } from "../../services/DialogService";

export default function Servicos() {
  const [servicos, setServicos] = useState([]);

  useDebugValue(() => {
    loadServicos();
  }, []);

  const openDialog = async(servico = null) => {
    const res = await DialogService.open(Servico, {servicoId: servico.id});

    if(res){
      loadServicos();
    }
  };

  const loadServicos = async () => {
    const {data} = await ServicoService.findAll();
    setServicos(data);
  };

  const renderTable = () => {
    console.log(1);
    
  };

  return (
    <Container style={{paddingTop: '30px'}}>

      <Grid container spacing={3}>

        <Grid item xs={6}>

          <FormControl fullWidth>

            <InputLabel>Pesquisar</InputLabel>
            <Input id="pesquisa" />

          </FormControl>

          <Grid item xs={6} style={{dusplay: "flex", alignItems: "flex-end", justifyContent: "flex-end"}}>
            <Button variant="contained" color="primary" onClick={openDialog}>Cadastrar</Button>
          </Grid>

          <Grid item xs={12}>
            {renderTable}
          </Grid>

        </Grid>
        
      </Grid>

    </Container>
  )
}
